mx-nav.data:

{
	list:[
		{
			do: "job1",
			text: "Nav1",
		},
		{
			do: "job2",
			text: "Nav2",
		}
	],
	do:{
		job1: function(){
			console.log('do job1');
		},
		job2: function(){
			console.log('do job2');
		},
	},
	selected: 1,
	"background-color": "#CCC",
	"text-color": "#333",
	"hover-background-color": "#EEE",
	"hover-text-color": "#555",
	"selected-background-color": "#F0F0F0",
	"selected-text-color": "#000",
}

mx-nav.css:

.mx-nav ul{
	list-style: none;
	margin:0;
	padding:0;
}

.mx-nav ul li{
	display: inline;
	height: 1.6em;
	line-height: 1em;
	margin:0;
	padding:0.3em;
	margin-left:0.3em;
	background: {$background-color};
	color: {$text-color};
	border-radius: 3px;
}

.mx-nav ul li:hover{
	background: {$hover-background-color};
	color: {$hover-text-color};
	cursor: pointer;
}

.mx-nav ul li.selected{
	background: {$selected-background-color};
	color: {$selected-text-color};
}

mx-nav.preload:

var html = '<ul>';
for (var i = 0;i < $data.list.length;i++){
	html += '<li do = "' + $data.list[i].do + '"';
	if (i == ($data.selected - 1)){
		html += ' class = "selected"';
	}
	html += '>' + $data.list[i].text + '</li>';
}
html += '</ul>';
$this.innerHTML = html;

mx-nav.callback:

var liGroup = $this.getElementsByTagName('li'),
	eraseClass = function(){
		for (var i = 0;i < liGroup.length;i++){
			liGroup[i].className = liGroup[i].className.replace(/selected/gm,'');
		}
	}
for (var i = 0;i < liGroup.length;i++){
	liGroup[i].onclick = function(){
		eraseClass();
		this.className += ' selected';
		$data.do[this.attributes['do'].value]();
	}
}

