	color: #555;
	cursor: pointer;
}

mxHeader.callback:

var navElement = $this.querySelectorAll('.nav')[0],
	navList = $data.nav.list,
	navHTML = '<ul>';
for (var i in navList){
	navHTML += '<li value = "' + navList[i] + '" class = "option">' + navList[i] + '</li>';
}
navHTML += '</ul>';
navElement.innerHTML = navHTML;

