mxNav.preload:

var preloadHTML = '<ul id = "navList">';
for (var i = 0;i < $data.list.length;i++){
	preloadHTML += '<li value = "' + $data.list[i] + '">' + $data.list[i] + '</li>';
}
preloadHTML += '</ul>';
$data.preloadHTML = preloadHTML;

